import React from 'react';
import { useState, useEffect } from 'react';
import CODEPROJECTS from '../../services/CodeProjectsJSON';



const FilterButtons = () => {

  const [filter, setFilter] = useState('all');
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    setProjects(portfolio);
  }, []);
  useEffect(() => {
    setProjects([]);
    const filtered = portfolio.map(p => ({ ...p, filtered: p.category.includes(filter) }));
    setProjects(filtered);
  }, [filter]);

    return (

          <div className="button-container">
            <button className="button button-all"
                    id="all"
                    onClick = { this.handleAllprojects }
                    >
              All projects
            </button>
            <button className="button button-React"
                    id="react"
                   //onClick={() => setFilterProjects(react)}
                   >
              React
            </button>
            <button className="button button-JS"
                    id="js"
                   // onClick={ handleJSProjects }
