import React from 'react';
import Character from './Character.js';
//import { Link } from 'react-router-dom';

class CharacterList extends React.Component {
    constructor(props) {
      super(props);
    }

render() {
    return (
        <ul>
        {this.props.data
            .filter(characterObject => this.props.inputValue === '' || characterObject.results.name.toLowerCase().includes(this.props.inputValue.toLowerCase()))
            .map(characterObject =>

            <li>

                    <Character key = {characterObject.id}
                               image={characterObject.image}
                               name={characterObject.name}
                               specie={characterObject.specie}
                    />

            </li>
            )}
        </ul>
    )
}
}

export default CharacterList;
