import React from 'react';
import '../styles/App.scss';
import fetchCharacters from './services/FetchData.js';
import CharacterList from './CharacterList';
import CharacterDetails from './CharacterDetails';
import FilterInput from './FilterInput';
import { Route, Switch } from 'react-router-dom';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.handleInputValue = this.handleInputValue.bind(this);
    this.renderCharacterDetails = this.renderCharacterDetails.bind(this);
    this.state = {
      data: [],
      value:''
        }
  }


  componentDidMount(){
    fetchCharacters()
      .then(data => {
        this.setState ({
          data:data.results
        })
      })
    }

    handleInputValue(inputValue){
      this.setState({
        value: inputValue
      })
    }

    renderCharacterDetails(props){
      console.log(props)
      const routeId = props.match.params.id;
      const characters = this.state.data;
      for(let cardCharacter of dataCardCharacter) {
        if(cardCharacter.id === parseInt(urlId)) {
          return <CharacterDetails character={cardCharacter}/>
        }
      }
    }

    renderDrinkDetail(props){
      console.log(props)
      //esto me lo pasa router son datos que nos llegan de la url
      const routeId = props.match.params.id;
      const drinks = this.state.data;
      //recorro mi estado si el id del objeto coincide con el id que me llega de la url retorno el componente DrinkDetails con la info
      //correspondiente, darse cuenta que lo que mando es el objeto entero a través de props
      for (let drink of drinks){
        if(drink.idDrink === routeId){
          return <DrinkDetails drinkObj={drink}/>
        }
      }
    }




  render(){
    console.log(this.state.data)
    return (
     <div>
       <Switch>
         <Route exact path="/">
           <FilterInput handleInputValue={this.handleInputValue}/>
           <CharacterList data={this.state.data} 
                          inputValue={this.state.value}/>
         </Route>
         <Route path="/cardCharacter:id" render={this.renderCharacterDetail}/>
      </Switch>
     </div>
    );
  }
}

export default App;
